mapper.select:
select
, ui.user_account, ui.head_picture_url, ui.phone_number


    ORDER BY
    ${sortProp}

        ${sortType}

mapper.sql:
from assets_turnover t
join user_info ui on ui.id = t.user_id
where 1 = 1

    and t.create_time >= #{startTime}


    and t.create_time <= #{endTime}


    and t.turnover_type = #{param.turnoverType}




            AND source_id = '1'


            AND target_id = '1'




    and t.settlement_id = #{param.settlementId}


    and ui.phone_number LIKE CONCAT('%',#{param.phoneNumber},'%')


    and ui.user_account LIKE CONCAT('%',#{param.userAccount},'%')

mapper.select:
SELECT
a.id, a.user_id, a.turnover_type,a.turnover_title, a.turnover_amount, a.after_amount,a.source_id,
a.target_id, a.settlement_id, a.create_time, a.detail_str,a.in_out_type,b.user_name,c.status as charge_status
FROM assets_turnover a
LEFT JOIN user_info b ON a.user_id = b.id
LEFT JOIN user_assets_charge c on a.charge_id = c.id
WHERE a.user_id = #{userId}

    AND a.turnover_type = #{turnoverType}


    AND a.settlement_id = #{settlementId}

ORDER BY a.create_time DESC

SELECT
a.id, a.user_id, a.turnover_type,a.turnover_title, a.turnover_amount, a.after_amount,a.source_id,
a.target_id, a.settlement_id, a.create_time, a.detail_str,a.in_out_type,b.user_name
FROM assets_turnover a LEFT JOIN user_info b ON a.user_id = b.id
WHERE a.user_id = #{userId}

    AND a.turnover_type = #{turnoverType}


    AND a.settlement_id = #{settlementId}




            AND a.source_id = #{userId}


            AND a.target_id = #{userId}




ORDER BY a.create_time DESC
